import React, { useState, useEffect, useRef } from 'react';
import { LineChart, Line, XAxis, YAxis, Tooltip, ResponsiveContainer, CartesianGrid } from 'recharts';
import {
    Box,
    Typography,
    IconButton,
    Card,
    CardContent,
    Grid,
    LinearProgress,
    Fade
} from '@mui/material';
import { Play, Pause, Volume2, VolumeX } from 'lucide-react';

// Sonidos
const goalSounds = {
    regular: new Audio('/sounds/regular-goal.mp3'),
    penalty: new Audio('/sounds/penalty.mp3'),
    freekick: new Audio('/sounds/freekick.mp3'),
    header: new Audio('/sounds/header.mp3')
};

Object.values(goalSounds).forEach(sound => {
    sound.volume = 0.5; // Ajustar volumen al 50%
});

const CareerPhases = {
    'Formación (2002-2006)': {
        color: '#4CAF50',
        description: 'Inicio de carrera en Sporting CP y primeros años en Manchester United',
        detailedDescription: `Durante esta etapa formativa, Cristiano Ronaldo emergió como una joven promesa en el Sporting CP, 
    donde debutó profesionalmente en 2002. Su talento excepcional llamó la atención del Manchester United, 
    especialmente después de un partido amistoso contra el club inglés. En 2003, bajo la tutela de Sir Alex Ferguson, 
    se unió al United por £12.24 millones, siendo en ese momento el adolescente más caro en el fútbol inglés. 
    En sus primeros años en Old Trafford, desarrolló su característico estilo de juego, combinando velocidad, 
    habilidad técnica y efectividad goleadora.`,
        sound: 'regular'
    },
    'Ascenso (2007-2009)': {
        color: '#2196F3',
        description: 'Consolidación en Manchester United y primer Balón de Oro',
        detailedDescription: `Este período marca la transformación de Ronaldo de promesa a superestrella mundial. 
    La temporada 2007-08 fue particularmente destacada, anotando 42 goles en todas las competiciones y 
    liderando al Manchester United hacia el doblete de Premier League y Champions League. En 2008, ganó su 
    primer Balón de Oro, reconociendo su dominio en el fútbol mundial. Su evolución física y técnica alcanzó 
    nuevos niveles, estableciendo récords de goles y desarrollando su famoso estilo de tiro libre. Esta etapa 
    consolidó su reputación como uno de los mejores jugadores del mundo.`,
        sound: 'header'
    },
    'Dominio (2010-2018)': {
        color: '#FFC107',
        description: 'Era dorada en Real Madrid',
        detailedDescription: `La etapa en el Real Madrid representa el apogeo de la carrera de Cristiano. Tras su 
