import React from 'react';
import { Box, Typography, Grid, Avatar } from '@mui/material';
import SportsScoreIcon from '@mui/icons-material/SportsScore';
import DirectionsRunIcon from '@mui/icons-material/DirectionsRun';
import SportsKabaddiIcon from '@mui/icons-material/SportsKabaddi';
import AccessibilityNewIcon from '@mui/icons-material/AccessibilityNew';
import AdjustIcon from '@mui/icons-material/Adjust';
import SportsSoccerIcon from '@mui/icons-material/SportsSoccer';

const GoalTypes = ({ data }) => {
  const icons = {
    "penales": <SportsScoreIcon />,
    "cabeza": <SportsKabaddiIcon />,
    "pie_derecho": <DirectionsRunIcon />,
    "pie_izquierdo": <AccessibilityNewIcon />,
    "tiros_libres": <AdjustIcon />,
    "in_game": <SportsSoccerIcon />
  };

  const playSiuuu = (goals) => {
    const audio = new Audio('/Ronaldo SIUUU Sound Effect.mp3');
    audio.volume = Math.min(goals / 500, 1);
    audio.play();
  };

  return (
    <Box>
      <Typography variant="h5" gutterBottom style={{ color: '#1E90FF' }}>
        Tipos de Goles
      </Typography>
      <Grid container spacing={2}>
        {Object.entries(data).map(([type, count]) => (
          <Grid item xs={6} sm={4} key={type}>
            <Avatar
              style={{ backgroundColor: '#1E90FF', cursor: 'pointer' }}
              onClick={() => playSiuuu(count)}
            >
              {icons[type]}
            </Avatar>
            <Typography align="center" style={{ color: '#FFFFFF' }}>{type.replace('_', ' ')}</Typography>
            <Typography align="center" variant="h6" style={{ color: '#4682B4' }}>{count}</Typography>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default GoalTypes;
