import React from 'react';
import { Paper, Typography, Grid, Avatar } from '@mui/material';
import { SportsScore, DirectionsRun, SportsKabaddi, AccessibilityNew, Adjust, SportsSoccer } from '@mui/icons-material';

const GoalTypes = ({ data }) => {
  const icons = {
    "penales": <SportsScore />,
    "cabeza": <SportsKabaddi />,
    "pie_derecho": <DirectionsRun />,
    "pie_izquierdo": <AccessibilityNew />,
    "tiros_libres": <Adjust />,
    "in_game": <SportsSoccer />
  };

  const playSiuuu = (goals) => {
    const audio = new Audio('/Ronaldo SIUUU Sound Effect.mp3');
    audio.volume = Math.min(goals / 500, 1);
    audio.play();
  };

  return (
    <Paper style={{ padding: '20px', backgroundColor: 'rgba(10, 25, 47, 0.7)' }}>
      <Typography variant="h5" gutterBottom style={{ color: '#1E90FF' }}>
        Tipos de Goles
      </Typography>
      <Grid container spacing={2}>
        {Object.entries(data).map(([type, count]) => (
          <Grid item xs={6} sm={4} key={type}>
            <Avatar
              style={{ backgroundColor: '#1E90FF', cursor: 'pointer' }}
              onClick={() => playSiuuu(count)}
            >
              {icons[type]}
            </Avatar>
            <Typography align="center">{type.replace('_', ' ')}</Typography>
            <Typography align="center" variant="h6">{count}</Typography>
          </Grid>
        ))}
      </Grid>
    </Paper>
  );
};

export default GoalTypes;
