import React, { useState, useEffect, useRef } from 'react';
import { LineChart, Line, XAxis, YAxis, Tooltip, ResponsiveContainer, CartesianGrid } from 'recharts';
import {
    Box,
    Typography,
    IconButton,
    Card,
    CardContent,
    Grid,
    LinearProgress,
    Fade
} from '@mui/material';
import { Play, Pause, Volume2, VolumeX } from 'lucide-react';

// Sonidos
const goalSounds = {
    regular: new Audio('/sounds/regular-goal.mp3'),
    penalty: new Audio('/sounds/penalty.mp3'),
    freekick: new Audio('/sounds/freekick.mp3'),
    header: new Audio('/sounds/header.mp3')
};

Object.values(goalSounds).forEach(sound => {
    sound.volume = 0.5; // Ajustar volumen al 50%
});

const CareerPhases = {
    'Formación (2002-2006)': {
        color: '#4CAF50',
        description: 'Inicio de carrera en Sporting CP y primeros años en Manchester United',
        detailedDescription: `Durante esta etapa formativa, Cristiano Ronaldo emergió como una joven promesa en el Sporting CP, 
      donde debutó profesionalmente en 2002. Su talento excepcional llamó la atención del Manchester United, 
      especialmente después de un partido amistoso contra el club inglés. En 2003, bajo la tutela de Sir Alex Ferguson, 
      se unió al United por £12.24 millones, siendo en ese momento el adolescente más caro en el fútbol inglés. 
      En sus primeros años en Old Trafford, desarrolló su característico estilo de juego, combinando velocidad, 
      habilidad técnica y efectividad goleadora.`,
        sound: 'regular'
    },
    'Ascenso (2007-2009)': {
        color: '#2196F3',
        description: 'Consolidación en Manchester United y primer Balón de Oro',
        detailedDescription: `Este período marca la transformación de Ronaldo de promesa a superestrella mundial. 
      La temporada 2007-08 fue particularmente destacada, anotando 42 goles en todas las competiciones y 
      liderando al Manchester United hacia el doblete de Premier League y Champions League. En 2008, ganó su 
      primer Balón de Oro, reconociendo su dominio en el fútbol mundial. Su evolución física y técnica alcanzó 
      nuevos niveles, estableciendo récords de goles y desarrollando su famoso estilo de tiro libre. Esta etapa 
      consolidó su reputación como uno de los mejores jugadores del mundo.`,
        sound: 'header'
    },
    'Dominio (2010-2018)': {
        color: '#FFC107',
        description: 'Era dorada en Real Madrid',
        detailedDescription: `La etapa en el Real Madrid representa el apogeo de la carrera de Cristiano. Tras su 
      fichaje récord de £80 millones, transformó su juego para convertirse en un goleador aún más prolífico. 
      Durante este período, ganó cuatro Champions League adicionales, estableció numerosos récords de goles, 
      incluyendo el de máximo goleador histórico del Real Madrid, y coleccionó cuatro Balones de Oro más. 
      Su rivalidad con Messi definió una era del fútbol, mientras su promedio de goles superaba el de un gol 
      por partido. Sus actuaciones en momentos cruciales y su capacidad para decidir partidos importantes 
      lo convirtieron en una leyenda del club.`,
        sound: 'freekick'
    },
    'Madurez (2019-2022)': {
        color: '#9C27B0',
        description: 'Etapa en Juventus y regreso a Manchester United',
        detailedDescription: `En su etapa de madurez, Ronaldo demostró su capacidad para adaptarse y mantener un 
      alto nivel en nuevos desafíos. En la Juventus, ganó dos Series A y se convirtió en el máximo goleador 
      de la liga en 2021. Su regreso al Manchester United en 2021 fue recibido con gran expectación, donde 
      continuó demostrando su capacidad goleadora a pesar de las dificultades del equipo. Durante este período, 
      también superó el récord de goles internacionales con Portugal, consolidando su legado como uno de los 
      mayores goleadores de la historia del fútbol.`,
        sound: 'penalty'
    },
    'Etapa Final (2023-)': {
        color: '#FF5722',
        description: 'Periodo en Al-Nassr',
        detailedDescription: `Su movimiento a Al-Nassr marca una nueva fase en su carrera, convirtiéndose en uno 
      de los jugadores mejor pagados del mundo. En Arabia Saudita, Ronaldo continúa demostrando su capacidad 
      goleadora y su influencia en el fútbol global. Su presencia ha aumentado significativamente la visibilidad 
      de la liga saudí y continúa rompiendo récords, incluyendo el de ser el goleador más prolífico en la 
      historia del fútbol profesional. Esta etapa representa no solo sus logros deportivos continuos, sino 
      también su impacto en la globalización del fútbol.`,
        sound: 'regular'
    }
};
const getPhaseForYear = (year) => {
    if (year <= 2006) return 'Formación (2002-2006)';
    if (year <= 2009) return 'Ascenso (2007-2009)';
    if (year <= 2018) return 'Dominio (2010-2018)';
    if (year <= 2022) return 'Madurez (2019-2022)';
    return 'Etapa Final (2023-)';
};

const MainViz = ({ data }) => {
    const [selectedYear, setSelectedYear] = useState(null);
    const [selectedPhase, setSelectedPhase] = useState(null);
    const [isPlaying, setIsPlaying] = useState(false);
    const [isMuted, setIsMuted] = useState(false);
    const [playbackIndex, setPlaybackIndex] = useState(0);
    const playbackRef = useRef(null);

    const yearlyData = Object.entries(data.anos)
        .map(([year, goals]) => {
            const phase = getPhaseForYear(parseInt(year));
            return {
                year: parseInt(year),
                goals,
                phase,
                phaseColor: CareerPhases[phase].color
            };
        })
        .sort((a, b) => a.year - b.year);

    const totalGoals = Object.values(data.clubes).reduce((a, b) => a + b, 0);

    const playGoalSound = (goals, phase) => {
        if (!isMuted) {
            const sound = goalSounds[CareerPhases[phase].sound];
            sound.currentTime = 0;
            sound.play().catch(error => console.log('Error playing sound:', error));
        }
    };

    useEffect(() => {
        if (isPlaying) {
            const playStep = () => {
                if (playbackIndex < yearlyData.length) {
                    const currentData = yearlyData[playbackIndex];
                    setSelectedYear(currentData.year);
                    playGoalSound(currentData.goals, currentData.phase);
                    setPlaybackIndex(prev => prev + 1);
                    playbackRef.current = setTimeout(playStep, 1000);
                } else {
                    setIsPlaying(false);
                    setPlaybackIndex(0);
                }
            };

            playbackRef.current = setTimeout(playStep, 1000);
        }

        return () => {
            if (playbackRef.current) {
                clearTimeout(playbackRef.current);
            }
        };
    }, [isPlaying, playbackIndex]);

    const handlePlayPause = () => {
        if (!isPlaying && playbackIndex >= yearlyData.length) {
            setPlaybackIndex(0);
        }
        setIsPlaying(!isPlaying);
    };

    const handlePhaseClick = (phase) => {
        setSelectedPhase(phase === selectedPhase ? null : phase);
    };

    const handleDataClick = (data) => {
        if (data && data.activePayload) {
            const payload = data.activePayload[0].payload;
            setSelectedYear(payload.year);
            playGoalSound(payload.goals, payload.phase);
        }
    };

    return (
        <Box sx={{ maxWidth: '1200px', margin: '0 auto', p: 3 }}>
            <Typography variant="h3" align="center" gutterBottom sx={{ color: '#00BFFF' }}>
                La Carrera Goleadora de Cristiano Ronaldo
            </Typography>

            <Grid container spacing={3}>
                {/* Fases de Carrera */}
                <Grid item xs={12}>
                    <Card sx={{ bgcolor: 'rgba(0,0,0,0.6)', mb: 3 }}>
                        <CardContent>
                            <Grid container spacing={2}>
                                {Object.entries(CareerPhases).map(([phase, { color, description }]) => (
                                    <Grid item xs={12} md={2.4} key={phase}>
                                        <Box
                                            onClick={() => handlePhaseClick(phase)}
                                            sx={{
                                                cursor: 'pointer',
                                                p: 1,
                                                borderRadius: 1,
                                                bgcolor: selectedPhase === phase ? `${color}22` : 'transparent',
                                                '&:hover': { bgcolor: `${color}11` }
                                            }}
                                        >
                                            <Box sx={{ width: '100%', height: 4, bgcolor: color, mb: 1 }} />
                                            <Typography variant="body2" sx={{ color: color }}>
                                                {phase}
                                            </Typography>
                                        </Box>
                                    </Grid>
                                ))}
                            </Grid>
                        </CardContent>
                    </Card>
                </Grid>

                {/* Gráfico Principal */}
                <Grid item xs={12}>
                    <Card sx={{ bgcolor: 'rgba(0,0,0,0.6)' }}>
                        <CardContent>
                            <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2, mb: 2 }}>
                                <IconButton
                                    onClick={handlePlayPause}
                                    sx={{
                                        bgcolor: 'primary.main',
                                        '&:hover': { bgcolor: 'primary.dark' },
                                        color: 'white'
                                    }}
                                >
                                    {isPlaying ? <Pause /> : <Play />}
                                </IconButton>
                                <IconButton
                                    onClick={() => setIsMuted(!isMuted)}
                                    sx={{
                                        bgcolor: 'primary.main',
                                        '&:hover': { bgcolor: 'primary.dark' },
                                        color: 'white'
                                    }}
                                >
                                    {isMuted ? <VolumeX /> : <Volume2 />}
                                </IconButton>
                            </Box>

                            <ResponsiveContainer width="100%" height={400}>
                                <LineChart
                                    data={yearlyData}
                                    margin={{ top: 20, right: 30, left: 40, bottom: 20 }}
                                    onClick={handleDataClick}
                                >
                                    <CartesianGrid strokeDasharray="3 3" stroke="#444" />
                                    <XAxis
                                        dataKey="year"
                                        stroke="#fff"
                                        tickFormatter={(year) => `'${year.toString().slice(2)}`}
                                        label={{
                                            value: 'Año',
                                            position: 'bottom',
                                            offset: 0,
                                            fill: '#fff',
                                            fontSize: 16
                                        }}
                                        tick={{ fontSize: 14 }}
                                    />
                                    <YAxis
                                        stroke="#fff"
                                        label={{
                                            value: 'Goles por Año',
                                            angle: -90,
                                            position: 'insideLeft',
                                            offset: -10,
                                            fill: '#fff',
                                            fontSize: 16
                                        }}
                                        tick={{ fontSize: 14 }}
                                    />
                                    {/* ... resto del LineChart ... */}
                                </LineChart>
                            </ResponsiveContainer>
                        </CardContent>
                    </Card>
                </Grid>

                {/* Detalles */}
                {selectedYear && (
                    <Grid item xs={12}>
                        <Fade in>
                            <Card sx={{ bgcolor: 'rgba(0,0,0,0.6)' }}>
                                <CardContent>
                                    <Grid container spacing={3}>
                                        <Grid item xs={12} md={4}>
                                            <Typography variant="h6" gutterBottom>
                                                Año {selectedYear}
                                            </Typography>
                                            <Typography variant="body1">
                                                {yearlyData.find(d => d.year === selectedYear)?.goals} goles
                                            </Typography>
                                            <Typography variant="body2" sx={{ mt: 1, color: '#666' }}>
                                                {CareerPhases[getPhaseForYear(selectedYear)].description}
                                            </Typography>
                                        </Grid>

                                        <Grid item xs={12} md={8}>
                                            <Typography variant="subtitle1" gutterBottom>
                                                Proporción de goles por tipo en carrera completa
                                            </Typography>
                                            {Object.entries(data.tipos_goles).map(([tipo, cantidad]) => {
                                                const porcentaje = (cantidad / totalGoals) * 100;
                                                return (
                                                    <Box key={tipo} sx={{ mb: 2 }}>
                                                        <Typography variant="body2" sx={{ mb: 1 }}>
                                                            {tipo}: {cantidad} ({porcentaje.toFixed(1)}%)
                                                        </Typography>
                                                        <LinearProgress
                                                            variant="determinate"
                                                            value={porcentaje}
                                                            sx={{
                                                                height: 8,
                                                                borderRadius: 4,
                                                                bgcolor: 'rgba(255,255,255,0.1)',
                                                                '& .MuiLinearProgress-bar': {
                                                                    bgcolor: CareerPhases[getPhaseForYear(selectedYear)].color
                                                                }
                                                            }}
                                                        />
                                                    </Box>
                                                );
                                            })}
                                        </Grid>
                                    </Grid>
                                </CardContent>
                            </Card>
                        </Fade>
                    </Grid>
                )}
            </Grid>
        </Box>
    );
};

export default MainViz;
