import React from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import YearlyGoals from './components/YearlyGoals';
import ClubGoals from './components/ClubGoals';
import GoalTypes from './components/GoalTypes';
import LeagueGoals from './components/LeagueGoals';
import backgroundImage from './cristiano-ronaldo-hd.png';
import data from './data.json';

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#1E90FF',
    },
    secondary: {
      main: '#4682B4',
    },
    background: {
      default: 'rgba(0, 0, 0, 0.7)',
      paper: 'rgba(0, 0, 0, 0.5)',
    },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    h2: {
      fontWeight: 700,
    },
  },
});

function App() {
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <div style={{
        backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.7)), url(${backgroundImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        minHeight: '100vh',
        padding: '20px',
      }}>
        <Container maxWidth="lg">
          <Typography variant="h2" align="center" gutterBottom style={{ color: '#1E90FF', textShadow: '2px 2px 4px rgba(0,0,0,0)' }}>
            Cristiano Ronaldo: Estad√≠sticas de Goles
          </Typography>
          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <YearlyGoals data={data.anos} />
            </Grid>
            <Grid item xs={12} md={6}>
              <ClubGoals data={data.clubes} />
            </Grid>
            <Grid item xs={12} md={6}>
              <GoalTypes data={data.tipos_goles} />
            </Grid>
            <Grid item xs={12} md={6}>
              <LeagueGoals data={data.ligas} />
            </Grid>
          </Grid>
        </Container>
      </div>
    </ThemeProvider>
  );
}

export default App;
