import React from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import YearlyGoals from './components/YearlyGoals';
import ClubGoals from './components/ClubGoals';
import GoalTypes from './components/GoalTypes';
import LeagueGoals from './components/LeagueGoals';
import backgroundImage from './cristiano-ronaldo-hd.png';
import data from './data.json';

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#1E90FF',
    },
    secondary: {
      main: '#4682B4',
    },
    background: {
      default: 'rgba(0, 0, 0, 0.7)',
      paper: 'rgba(0, 0, 0, 0.5)',
    },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    h2: {
      fontWeight: 700,
    },
  },
});

function App() {
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box
        sx={{
          backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.7)), url(${backgroundImage})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          minHeight: '100vh',
          padding: '20px',
          display: 'flex',
          flexDirection: 'column',
        }}
      >
        <Typography variant="h2" align="center" gutterBottom style={{ color: '#1E90FF', textShadow: '2px 2px 4px rgba(0,0,0,0.5)' }}>
          Cristiano Ronaldo: Estad√≠sticas de Goles
        </Typography>
        <Box
          sx={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
            gap: '20px',
            flexGrow: 1,
          }}
        >
          <YearlyGoals data={data.anos} />
          <ClubGoals data={data.clubes} />
          <GoalTypes data={data.tipos_goles} />
          <LeagueGoals data={data.ligas} />
        </Box>
      </Box>
    </ThemeProvider>
  );
}

export default App;
