import React, { useState } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import InteractiveGoalsViz from './components/InteractiveGoalsViz';
import ClubGoals from './components/ClubGoals';
import GoalTypes from './components/GoalTypes';
import LeagueGoals from './components/LeagueGoals';
import backgroundImage from './cristiano-ronaldo-hd.png';
import data from './data.json';

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#00BFFF',
    },
    secondary: {
      main: '#FFFFFF',
    },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    h2: {
      fontWeight: 700,
      fontSize: '2.5rem',
      color: '#00BFFF',
    },
    h4: {
      fontSize: '2rem',
      fontWeight: 600,
      color: '#00BFFF',
    },
    h5: {
      fontSize: '1.5rem',
      fontWeight: 600,
      color: '#00BFFF',
    },
    h6: {
      fontSize: '1.2rem',
      color: '#FFFFFF',
    },
    body1: {
      fontSize: '1.1rem',
      color: '#FFFFFF',
    },
    body2: {
      fontSize: '1rem',
      color: '#FFFFFF',
    },
  },
});

function App() {
  const [selectedView, setSelectedView] = useState('overview');

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box
        sx={{
          backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.8), rgba(0, 0, 0, 0.8)), url(${backgroundImage})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          minHeight: '100vh',
          padding: '20px',
          display: 'flex',
          flexDirection: 'column',
        }}
      >
        <Typography variant="h2" align="center" gutterBottom>
          Cristiano Ronaldo: 700 Goles por Club hasta 2023
        </Typography>

        <Box className="flex justify-center space-x-4 mb-8">
          <button
            className={`px-4 py-2 rounded-lg ${selectedView === 'overview' ? 'bg-blue-500' : 'bg-gray-700'
              }`}
            onClick={() => setSelectedView('overview')}
          >
            Vista General
          </button>
          <button
            className={`px-4 py-2 rounded-lg ${selectedView === 'interactive' ? 'bg-blue-500' : 'bg-gray-700'
              }`}
            onClick={() => setSelectedView('interactive')}
          >
            Explorar Datos
          </button>
        </Box>

        {selectedView === 'overview' ? (
          <Grid container spacing={4} sx={{ flexGrow: 1 }}>
            <Grid item xs={12} md={6}>
              <ClubGoals data={data.clubes} />
            </Grid>
            <Grid item xs={12} md={6}>
              <GoalTypes data={data.tipos_goles} />
            </Grid>
            <Grid item xs={12}>
              <LeagueGoals data={data.ligas} />
            </Grid>
          </Grid>
        ) : (
          <InteractiveGoalsViz data={data} />
        )}
      </Box>
    </ThemeProvider>
  );
}

export default App;
