import React from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import YearlyGoals from './components/YearlyGoals';
import ClubGoals from './components/ClubGoals';
import GoalTypes from './components/GoalTypes';
import LeagueGoals from './components/LeagueGoals';
import backgroundImage from './cristiano-ronaldo-hd.png';
import data from './data.json';

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#1E90FF',
    },
    secondary: {
      main: '#4682B4',
    },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    h2: {
      fontWeight: 700,
      fontSize: '2.5rem',
      '@media (min-width:600px)': {
        fontSize: '3rem',
      },
    },
    h5: {
      fontSize: '1.2rem',
      '@media (min-width:600px)': {
        fontSize: '1.5rem',
      },
    },
  },
});

function App() {
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box
        sx={{
          backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.7)), url(${backgroundImage})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundAttachment: 'fixed',
          minHeight: '100vh',
          padding: '20px',
          display: 'flex',
          flexDirection: 'column',
        }}
      >
        <Typography variant="h2" align="center" gutterBottom style={{ color: '#1E90FF', textShadow: '2px 2px 4px rgba(0,0,0,0.5)' }}>
          Cristiano Ronaldo: Estad√≠sticas de Goles
        </Typography>
        <Grid container spacing={2} sx={{ 
          flexGrow: 1, 
          alignItems: 'stretch',
          backgroundColor: 'rgba(0, 0, 0, 0.7)',
          borderRadius: '15px',
          padding: '20px',
          margin: '0 auto',
          maxWidth: '95%'
        }}>
          <Grid item xs={12} md={6} sx={{ display: 'flex' }}>
            <YearlyGoals data={data.anos} />
          </Grid>
          <Grid item xs={12} md={6} sx={{ display: 'flex' }}>
            <ClubGoals data={data.clubes} />
          </Grid>
          <Grid item xs={12} md={6} sx={{ display: 'flex' }}>
            <GoalTypes data={data.tipos_goles} />
          </Grid>
          <Grid item xs={12} md={6} sx={{ display: 'flex' }}>
            <LeagueGoals data={data.ligas} />
          </Grid>
        </Grid>
      </Box>
    </ThemeProvider>
  );
}

export default App;
