import React from 'react';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts';
import { Paper, Typography } from '@mui/material';

const YearlyGoals = ({ data }) => {
  const chartData = Object.entries(data).map(([year, goals]) => ({ year, goals }));

  return (
    <Paper style={{ padding: '20px', backgroundColor: 'rgba(0, 0, 0, 0.5)' }}>
      <Typography variant="h5" gutterBottom style={{ color: '#1E90FF' }}>
        Goles por AÃ±o
      </Typography>
      <ResponsiveContainer width="100%" height={400}>
        <BarChart data={chartData} layout="vertical" margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
          <XAxis type="number" tick={{ fill: 'white' }} />
          <YAxis dataKey="year" type="category" width={40} tick={{ fill: 'white' }} />
          <Tooltip contentStyle={{ backgroundColor: 'rgba(0, 0, 0, 0)', color: 'white' }} />
          <Bar dataKey="goals" fill="#1E90FF" label={{ position: 'right', fill: 'white' }} />
        </BarChart>
      </ResponsiveContainer>
    </Paper>
  );
};

export default YearlyGoals;
